$(document).ready(function(){$("[data-bs-hover-animate]").mouseenter(function(){var t=$(this);t.addClass("animated "+t.attr("data-bs-hover-animate"))}).mouseleave(function(){var t=$(this);t.removeClass("animated "+t.attr("data-bs-hover-animate"))})}),$(document).ready(function(){$("#list").click(function(t){t.preventDefault(),$("#products .item").addClass("list-group-item")}),$("#grid").click(function(t){t.preventDefault(),$("#products .item").removeClass("list-group-item"),$("#products .item").addClass("grid-group-item")})});